const folder = {
    name: "folder",
    template: '#folder-component',
    props: {
        folder: Object
    },
    methods: {
        expand() {
            if ( this.folder.leaf ) {
                return;
            }
            this.folder.expanded = !this.folder.expanded;
        }
    }
}

const folders = {
    name: 'root',
    template: '#folders-component',
    props: {
        folder: Object
    },
    components: {
        'folder': folder
    }
}

